function get_tokens(){ $.ajax({ url: '<?php echo $base_url; ?>get-token/<?php echo $form_id; ?>/', type: 'GET', contentType: false, processData: false, success: function(data){ var obj = JSON.parse(data); var response_code = obj['response']; var message = obj['message']; var session = obj['session']; $('.csrf_token-<?php echo $form_id; ?>').val(message); $('.session-<?php echo $form_id; ?>').val(session); } }); return false;}
